import React, { useState } from "react";
import classes from "./MealItemForm.module.css";
import Input from "../../UI/Input";

function MealItemForm({ id, onSubmit }) {
  const [enteredValue, setEnteredValue] = useState(1);
  const [isValid, setIsValid] = useState(true);

  const changeHandler = (e) => {
    setEnteredValue(e.target.value);
    console.log(enteredValue);
  };

  const submitHandler = (e) => {
    e.preventDefault();
    const entVal = +enteredValue;
    if (entVal.trim().length === 0 || entVal < 1 || entVal > 5) {
      setIsValid(false)
      return;
    }

    onSubmit(entVal);
  };

  return (
    <form onSubmit={submitHandler} className={classes.form}>
      <Input
        label="Amount"
        input={{
          id: "amount" + id,
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: enteredValue,
          onChange: changeHandler,
        }}
      />
      <button>+ Add</button>
      {!isValid && <p>Please enter a valid amount (0</p>}
    </form>
  );
}

export default MealItemForm;
